import axios from "axios";
import { Baseurl } from "../../baseUrl";

const btcRateReducer = (state = '', action) => {
    let response = '';
    axios.get(Baseurl, {}).then((res)=>{
       response = res.data.bpi.NGN.rate     
        })
    switch (action.type) {
      case "RATE":
        return state = response 
      default:
        return state;
    }
  };
  export default btcRateReducer;
