import axios from "axios";
import { Baseurl } from "../../baseUrl";

const btcRateReducer = (state = '', action) => {
    switch (action.type) {
      case "RATE":
        axios.get(Baseurl, {}).then((res)=>{
           state = res.data.bpi.NGN.rate     
            })
        return state  
      default:
        return state;
    }
  };
  export default btcRateReducer;
