import React, { useEffect } from 'react'
import { useDispatch, useSelector} from 'react-redux'
import { btc } from '../redux/actions'
import { eth } from '../redux/actions'
import axios from 'axios'
import { Baseurl } from '../baseUrl'
import Moment from 'react-moment'


const Rate = () => {
    const dispatch = useDispatch()
    useEffect(() => {
    axios.get(Baseurl, {}).then((res)=>{
      if(res.data){
        let response = res.data;
        let btcData = response.find(
          (data) => data.name === "Bitcoin"
        );
        if(btcData){
        dispatch(btc(btcData))
        }
       
        let ethData = response.find(
          (data) => data.name === "Ethereum"
        );
        if(ethData){
        dispatch(eth(ethData))
        }
      }else{
        dispatch(btc(null))
        dispatch(eth(null))
      }
        }).catch((error)=>{
          console.log(error.response)

        })
    }, [])

const btcState = useSelector(state => state.btc)
const ethState = useSelector(state => state.eth)
    return (
        <React.Fragment>
             <div class="leaders bg-overlay p-3" data-aos="fade-zoom-in">
              <h2 class="text-white mb-5">Market Leader</h2>

              <div class="d-flex leaders-coin p-3 mb-3">
                <div class="mb-0 col-3 text-white">Bitcoin (BTC)</div>
                <div class="mb-0 col-6 text-white"></div>
                <div class="col-3 text-white text-end">
                  <h4 class="mb-0">${btcState.current_price}</h4>
                  <small className='text-dark' style={{}}><strong>Updated:<Moment fromNow>{btcState.last_updated}</Moment></strong></small>
                </div>
              </div>

              <div class="d-flex leaders-coin p-3 mb-3">
                <div class="mb-0 col-3 text-white">Ethereum (ETH)</div>
                <div class="mb-0 col-6 text-white"></div>
                <div class="mb-0 col-3 text-white text-end">
                  <h4 class="mb-0">${ethState.current_price}</h4>
                  <small className='text-dark'><strong>Updated: {ethState.last_updated}</strong></small>
            
                </div>
              </div>
            </div>
            
        </React.Fragment>
    )
}

export default Rate;
